import React, { useState } from 'react'
import './signIn.css'

const API = process.env.REACT_APP_DISTRITO7;

const SignIn = () => {

    // Company
    // Professor
    // Student 
    // Manager
    // Person 

    // Id User
    // Name
    // Email
    // Password
    // Address

    const [name, setName] = useState("");
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [address, setAddress] = useState("")
    const [type, setType] = useState("")

    const handleSubmit = async (e) => {
        e.preventDefault();
        const res = await fetch(`${API}/`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                name,
                email,
                password,
                address,
            })
        })
        const data = await res.json();
        console.log(data)
    }

    return (
        <div className='signin-whole'>
            <div className='signin-form'>
                <form onSubmit={handleSubmit} className='signin-form_body'>
                    <h1>GET IN AS YOU PLEASE</h1>
                    <div className='signin-form_body-input'>
                        <label>Name</label>
                        <input
                        type='text'
                        onChange={e => setName(e.target.value)}
                        value={name}
                        className='form-control'
                        placeholder='Name'
                        autoFocus/>
                    </div>
                    <div className='signin-form_body-input'>
                        <label>Email</label>
                        <input
                        type='text'
                        onChange={e => setEmail(e.target.value)}
                        value={email}
                        className='form-control'
                        placeholder='Email'/>
                    </div>
                    <div className='signin-form_body-input'>
                        <label>Password</label>
                        <input
                        type='password'
                        onChange={e => setPassword(e.target.value)}
                        value={password}
                        placeholder='Password'/>
                    </div>
                    <div className='signin-form_body-input'>
                        <label>Address</label>
                        <input
                        type='text'
                        onChange={e => setAddress(e.target.value)}
                        value={address}
                        placeholder='Address'
                        />
                    </div>
                    <div className='signin-form_body-input'>
                        
                            <select className='signin-form_body-input' onChange={e => setType(e.target.value)}>
                                    <option selected disabled>Category:</option>
                                    <option value="horror">Horror</option>
                                    <option value="suspense">Suspense</option>
                                    <option value="science-fiction">Sci-fi</option>
                                    <option value="psicological">Psicological</option>
                                    <option value="action">Action</option>
                                    <option value="romance">Romance</option>
                            </select>
                        
                    </div>
                </form>
            </div>
        </div>
    )
}

export default SignIn;
