import React, { useState, useEffect } from 'react'
import './signIn.css'

const SignIn = () => {

    // Company
    // Professor
    // Student 
    // Manager
    // Person 

    // Id User
    // Name
    // Email
    // Password
    // Address

    const [name, setName] = useState("");
    const [lastname, setlastName] = useState("");
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [addressZipCode, setAddressZipCode] = useState("")
    const [addressStreet, setAddressStreet] = useState("")
    const [addressCity, setAddressCity] = useState("")
    const [addressCountry, setAddressCountry] = useState("")
    const [addressDeparment, setAddressDeparment] = useState("")
    const [type, setType] = useState("")

    const [studentCode, setCode] = useState("")
    const [coursenumber, setCourse] = useState("")
    const [professor, setProfessor] = useState("")
    const [nitnumber, setNitnumber] = useState("")

    const handleSubmit = async (e) => {
        e.preventDefault();
        const res = await fetch(`/signIn${type}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                name,
                lastname,
                email,
                password,
                addressZipCode,
                addressCity,
                addressCountry,
                addressDeparment,
                addressStreet,
                studentCode,
                courseNumber,
                professor,
                nitnumber,
            })
        })
        const data = await res.json();
        console.log(data)
    }

    useEffect(()=>{
        if(coursenumber === '1493'){
            setProfessor("Plum")
        }else if(coursenumber === '1243'){
            setProfessor("Robert Langdon")
        }
    }, [coursenumber])
    
    function isOk(name, lastname, email, password, addressZipCode, addressCity, addressCountry, addressDeparment, addressStreet){
        if(name.trim() !== "" && lastname.trim() !== "" && email.trim() !== "" && password.trim() !== "" && addressZipCode.trim() !== "" && addressCity.trim() !== "" && addressCountry.trim() !== "" && addressDeparment.trim() !== "" && addressStreet.trim() !== ""){
            return true
        } else {
            return false
        } 
    }

    return (
        <div className='signin-whole'>
            <div className='signin-form'>
                <form onSubmit={handleSubmit} className='signin-form_body' method='post'>
                    <h1>GET IN AS YOU PLEASE</h1>
                    <div className='signin-form_body-input'>
                        <label>Name</label>
                        <input
                        type='text'
                        onChange={e => setName(e.target.value)}
                        value={name}
                        className='form-control'
                        placeholder='Name'
                        autoFocus/>
                    </div>
                    {
                       type !== 'company' ? (
                           <>
                           <div className='signin-form_body-input'>
                            <label>Last Name</label>
                            <input
                            type='text'
                            onChange={e => setlastName(e.target.value)}
                            value={lastname}
                            className='form-control'
                            placeholder='Last Name'
                            autoFocus/>
                        </div>
                           </>
                       ):(<></>)
                    }
                    <div className='signin-form_body-input'>
                        <label>Email</label>
                        <input
                        type='text'
                        onChange={e => setEmail(e.target.value)}
                        value={email}
                        className='form-control'
                        placeholder='Email'/>
                    </div>
                    <div className='signin-form_body-input'>
                        <label>Password</label>
                        <input
                        type='password'
                        onChange={e => setPassword(e.target.value)}
                        value={password}
                        placeholder='Password'/>
                    </div>
                    <div className='signin-form_body-input'>
                        <label>Address</label>
                        <input
                        type='text'
                        onChange={e => setAddressZipCode(e.target.value)}
                        value={addressZipCode}
                        placeholder='Zip Code'
                        />
                        <input
                        type='text'
                        onChange={e => setAddressStreet(e.target.value)}
                        value={addressStreet}
                        placeholder='Street'
                        />
                        <input
                        type='text'
                        onChange={e => setAddressCity(e.target.value)}
                        value={addressCity}
                        placeholder='City'
                        />
                        <input
                        type='text'
                        onChange={e => setAddressCountry(e.target.value)}
                        value={addressCountry}
                        placeholder='Country'
                        />
                        <input
                        type='text'
                        onChange={e => setAddressDeparment(e.target.value)}
                        value={addressDeparment}
                        placeholder='Department'
                        />
                    </div>
                    <div className='signin-form_body-input'>
                        <label>Account Type</label>
                        <select onChange={e => setType(e.target.value)}>
                                <option selected disabled>Category:</option>
                                <option value="student">Student</option>
                                <option value="company">Company</option>
                                <option value="professor">Professor</option>
                                <option disabled value="manager">Manager</option>
                                <option value="person">Person</option>
                        </select> 
                    </div>
                    <div>
                    {type === '' ? (
                        <>
                        </>
                    ): type === 'student' ? (
                        <>
                        <div className='signin_form-addition'>
                            <label>Student Code</label>
                            <input
                            type='text'
                            onChange={e => setCode(e.target.value)}
                            value={studentCode}
                            placeholder='Student Code'
                            />
                        </div>
                        <div className='signin_form-addition'>
                            <label>Course Number</label>
                            <select onChange={e => setCourse(e.target.value)}>
                                <option selected disabled>NRC:</option>
                                <option value="1493">1493</option>
                                <option value="1243">1243</option>
                                <option value="4930">4930</option>
                                <option value="2077">2077</option>
                                <option value="3072">3072</option>
                            </select>
                        </div>
                        <div className='signin_form-addition'>
                            <label>Professor</label>
                            {courseNumber === '1493' ? (
                                <h1>Plum</h1>

                            ):courseNumber === '1243' ? (
                                <h1>Robert Langdon</h1>
                                
                            ):courseNumber === '4930' ? (
                                <h1>Jacob Barnhardt</h1>
                                
                            ):courseNumber === '2077' ? (
                                <h1>John Keating</h1>
                        
                            ):courseNumber === '3072' ? (
                                <h1>Robin Williams</h1>
    
                            ):(
                                <>
                                </>
                            )}
                        </div>
                        </>
                    ): type === 'company' ? (
                        <>
                        <div className='signin_form-addition'>
                            <label>NIT NUMBER</label>
                            <input
                            type='text'
                            onChange={e => setNitnumber(e.target.value)}
                            value={nitnumber}
                            placeholder='Nit Number'
                            />
                        </div>
                        </>
                    ): type === 'professor' ? (
                        <>
                        <div className='signin_form-addition'>
                        <label>Course Number</label>
                            <select onChange={e => setCourse(e.target.value)}>
                                <option selected disabled>NRC:</option>
                                <option value="1493">1493</option>
                                <option value="1243">1243</option>
                                <option value="4930">4930</option>
                                <option value="2077">2077</option>
                                <option value="3072">3072</option>
                            </select>
                        </div>
                        </>
                    ):(
                        <>
                        </>
                    )}
                    {isOk(name, lastname, email, password, addressZipCode, addressCity, addressCountry, addressDeparment, addressStreet) ? (
                        <>
                        <button className="signin-form_body-input">
                            Next
                        </button>
                        </>
                    ):(
                        <>
                        </>
                    )}
                    </div>
                </form>
                
            </div>
        </div>
    )
}

export default SignIn;
