import React, {useEffect, useState} from 'react'

const CreateP = () => {

    const [name, setName] = useState('')
    const [submissionDate, setSubmission] = useState('')
    const [state, setState] = useState('')
    const [description, setDescription] = useState('')
    const [background, setBackground] = useState('')
    const [active, setActiveuser] = useState([])

    const handleSubmit = async (e) => {
        e.preventDefault();
        const res = await fetch(`/createP`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                name,
            })
        })
        const data = await res.json();
        console.log(data)
    }

    const getActive = async () => {
        const res = await fetch(`/getUser`)
        const data = await res.json();
        setActiveuser(data)
    }

    useEffect(() => {
        getActive();
    }, [])


    return (
        <div className='project-form-whole'>
            <div className='project-form'>
                <form onSubmit={handleSubmit} className='project-form-body' method='post'>
                    <div className='project-form-input'>
                    <label>Title</label>
                        <input
                        type='text'
                        onChange={e => setName(e.target.value)}
                        value={name}
                        className='form-control'
                        placeholder='Name'/>
                    </div>
                    <div className='project-form-input'>
                    <label>Author</label>
                    <input
                        type='text'
                        onChange={() => {}}
                        value={active['name'] + ' ' + active['lastname']}
                        />
                    </div>
                    <div className='project-form-input-description'>
                    <label>Author</label>
                    <input
                        type='text'
                        onChange={e => {e.target.value}}
                        value={active['name'] + ' ' + active['lastname']}
                        />
                    </div>
                </form>
            </div>
        </div>
    )
}

export default CreateP
