import React, { useState } from 'react'
import './signIn.css'

const SignIn = () => {

    // Company
    // Professor
    // Student 
    // Manager
    // Person 

    // Id User
    // Name
    // Email
    // Password
    // Address

    const [name, setName] = useState("");
    const [lastname, setlastName] = useState("");
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [addressZipCode, setAddressZipCode] = useState("")
    const [addressStreet, setAddressStreet] = useState("")
    const [addressCity, setAddressCity] = useState("")
    const [addressCountry, setAddressCountry] = useState("")
    const [addressDeparment, setAddressDeparment] = useState("")
    const [type, setType] = useState("")


    const [courseNumber, setCourse] = useState("")
    const [professor, setProfessor] = useState("")


    const handleSubmit = async (e) => {
        e.preventDefault();
        const res = await fetch(`/signIn`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                name,
                lastname,
                email,
                password,
                addressZipCode,
                addressCity,
                addressCountry,
                addressDeparment,
                addressStreet,
            })
        })
        const data = await res.json();
        console.log(data)
    }

    return (
        <div className='signin-whole'>
            <div className='signin-form'>
                <form onSubmit={handleSubmit} className='signin-form_body' method='post'>
                    <h1>GET IN AS YOU PLEASE</h1>
                    <div className='signin-form_body-input'>
                        <label>Name</label>
                        <input
                        type='text'
                        onChange={e => setName(e.target.value)}
                        value={name}
                        className='form-control'
                        placeholder='Name'
                        autoFocus/>
                    </div>
                    <div className='signin-form_body-input'>
                        <label>Last Name</label>
                        <input
                        type='text'
                        onChange={e => setlastName(e.target.value)}
                        value={lastname}
                        className='form-control'
                        placeholder='Last Name'
                        autoFocus/>
                    </div>
                    <div className='signin-form_body-input'>
                        <label>Email</label>
                        <input
                        type='text'
                        onChange={e => setEmail(e.target.value)}
                        value={email}
                        className='form-control'
                        placeholder='Email'/>
                    </div>
                    <div className='signin-form_body-input'>
                        <label>Password</label>
                        <input
                        type='password'
                        onChange={e => setPassword(e.target.value)}
                        value={password}
                        placeholder='Password'/>
                    </div>
                    <div className='signin-form_body-input'>
                        <label>Address</label>
                        <input
                        type='text'
                        onChange={e => setAddressZipCode(e.target.value)}
                        value={addressZipCode}
                        placeholder='Zip Code'
                        />
                        <input
                        type='text'
                        onChange={e => setAddressStreet(e.target.value)}
                        value={addressStreet}
                        placeholder='Street'
                        />
                        <input
                        type='text'
                        onChange={e => setAddressCity(e.target.value)}
                        value={addressCity}
                        placeholder='City'
                        />
                        <input
                        type='text'
                        onChange={e => setAddressCountry(e.target.value)}
                        value={addressCountry}
                        placeholder='Country'
                        />
                        <input
                        type='text'
                        onChange={e => setAddressDeparment(e.target.value)}
                        value={addressDeparment}
                        placeholder='Department'
                        />
                    </div>
                    <div className='signin-form_body-input'>
                        <label>Account Type</label>
                        <select onChange={e => setType(e.target.value)}>
                                <option selected disabled>Category:</option>
                                <option value="student">Student</option>
                                <option value="company">Company</option>
                                <option value="professor">Professor</option>
                                <option value="manager">Manager</option>
                                <option value="person">Person</option>
                        </select> 
                    </div>
                    <button className="signin-form_body-input">
                        Next
                    </button>
                    {type === '' ? (
                        <>
                        </>
                    ): type === 'student' ? (
                        <>
                        <h1>STUDENT SELECTED</h1>
                        <div className='signin_form-addition'>
                            <label>Course Number</label>
                            <select onChange={e => setCourse(e.target.value)}>
                                <option selected disabled>NRC:</option>
                                <option value="1493">1493</option>
                                <option value="1243">1243</option>
                                <option value="4930">4930</option>
                                <option value="2077">2077</option>
                                <option value="3072">3072</option>
                            </select>
                        </div>
                        <div className='signin_form-addition'>
                            <label>Professor</label>
                            <select onChange={e => setCourse(e.target.value)}>
                            {courseNumber === '1493' ? (
                                <>
                                <option selected disabled value="Plum">Plum</option>
                                </>
                            ):courseNumber === '1493' ? (
                                <>
                                <option selected disabled value="Plum">Plum</option>
                                </>
                            ):courseNumber === '1493' ? (
                                <>
                                <option selected disabled value="Plum">Plum</option>
                                </>
                            ):courseNumber === '1493' ? (
                                <>
                                </>
                            ):courseNumber === '1493' ? (
                                <>
                                </>
                            ):(
                                <>
                                </>
                            )}
        
                                <option selected disabled>Name</option>
                                <option value="Plum">Plum</option>
                                <option value="Robert Langdon">Robert Langdon</option>
                                <option value="Jacob Barnhardt">Jacob Barnhardt</option>
                                <option value="John Keating">John Keating</option>
                                <option value="Robin Williams">Robin Williams72</option>
                            </select>
                        </div>
                        </>
                    ): type === 'company' ? (
                        <>
                        <h1>COMPANY SELECTED</h1>
                        </>
                    ): type === 'professor' ? (
                        <>
                        <h1>PROFESSOR SELECTED</h1>
                        </>
                    ): type === 'manager' ? (
                        <>
                        <h1>MANAGER SELECTED</h1>
                        </>
                    ): type === 'person' ? (
                        <>
                        <h1>PERSON SELECTED</h1>
                        </>
                    ):(
                        <>
                        </>
                    )}
                </form>
            </div>
        </div>
    )
}

export default SignIn;
