import React,{useState} from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';


import Home from './pages/Home/Home.js'
import Projects from './pages/Projects/Projects.js'
import Organizations from './pages/Organizations/Organizations.js'
import FAQ from './pages/FAQ/FAQ.js'
import About from './pages/About/About.js'

import Navbar from './components/Navbar/Navbar';

let cam,
    scene,
    renderer,
    clock,
    smokeMaterial,
    h,
    w,
    smokeParticles = [];

    const animate = () => {
        let delta = clock.getDelta();

        requestAnimationFrame(animate);

        [].forEach.call(smokeParticles, (sp) => {
            sp.rotation.z += delta * 0.2;
        });

        renderer.render(scene, cam);
    },
    resize = () => {
        renderer.setSize(window.innerWidth, window.innerHeight);
    },
    lights = () => {
        const light = new THREE.DirectionalLight(0xffffff, 0.5);

        clock = new THREE.Clock();

        renderer = new THREE.WebGLRenderer();
        renderer.setClearColor(0x00547f, 1);
        renderer.setSize(w, h);

        scene = new THREE.Scene();

        light.position.set(-1, 0, 1);
        scene.add(light);
    },
    camera = () => {
        cam = new THREE.PerspectiveCamera(75, w / h, 1, 10000);

        cam.position.z = 1000;

        scene.add(cam);
    },
    action = () => {
        const loader = new THREE.TextureLoader();

        loader.crossOrigin = "";

        loader.load(
            "https://s3-us-west-2.amazonaws.com/s.cdpn.io/82015/blue-smoke.png",
            function onLoad(texture) {
                const smokeGeo = new THREE.PlaneBufferGeometry(300, 300);

                smokeMaterial = new THREE.MeshLambertMaterial({
                    map: texture,
                    transparent: true
                });

                for (let p = 0, l = 350; p < l; p++) {
                    let particle = new THREE.Mesh(smokeGeo, smokeMaterial);

                    particle.position.set(
                        Math.random() * 500 - 250,
                        Math.random() * 500 - 250,
                        Math.random() * 1000 - 100
                    );

                    particle.rotation.z = Math.random() * 360;
                    scene.add(particle);
                    smokeParticles.push(particle);
                }

                animate();
            }
        );
    },
    init = () => {
        h = window.innerHeight;
        w = window.innerWidth;

        lights(); //ðŸ’¡
        camera(); // ðŸŽ¥
        action(); // ðŸŽ¬

        document.body.appendChild(renderer.domElement);
        window.addEventListener("resize", resize);
    };


const App = () => {
  return (
    <div className='App'>
      <smoke/>
      <div>
        <Navbar/>
      </div>
      <div>
        <Router>
            <Route path= "/" exact component={Home}/>
            <Route path= "/projects" component={Projects}/>
            <Route path= "/organizations" component={Organizations}/>
            <Route path= "/FAQ" component={FAQ}/>
            <Route path= "/about" component={About}/>              
        </Router>
      </div>    
    </div>

  );
}

export default App;
